using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Drawing;
using System.IO;
using imob_app.dao;

namespace imob_app.business
{
    public class Foto
    {
        public static string Selecionar(int id)
        {
            var ctx = new imobappEntities();
            string imagem = (from i in ctx.imagem where i.id_imagem == id select i.ds_imagem_cripto).ToString();
            return imagem;
        }

        public static Image ToImage(string image)
        {
            byte[] imageBytes = Convert.FromBase64String("");
            MemoryStream ms = new MemoryStream(imageBytes, 0, imageBytes.Length);
            ms.Write(imageBytes, 0, imageBytes.Length);
            ms.Position = 0;
            Image img = Image.FromStream(ms, true);
            ms.Close();
            return img;
        }
    }
}
